// src/AudioProcessor.js
import React, { useState, useRef } from 'react';
import styled from 'styled-components';

const Container = styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
  width: 100%;
  padding: 20px;
`;

const Button = styled.button`
  font-size: 16px;
  padding: 10px 20px;
  margin: 10px;
  cursor: pointer;
  background-color: #4caf50;
  color: white;
  border: none;
  border-radius: 5px;
  &:hover {
    background-color: #45a049;
  }
`;

const InputLabel = styled.label`
  display: inline-block;
  font-size: 16px;
  padding: 10px 20px;
  margin: 10px;
  cursor: pointer;
  background-color: #008cba;
  color: white;
  border: none;
  border-radius: 5px;
  &:hover {
    background-color: #007bb5;
  }
`;

const HiddenInput = styled.input`
  display: none;
`;

const AudioContainer = styled.div`
  margin: 20px 0;
  text-align: center;
`;

const AudioTitle = styled.h2`
  font-size: 18px;
  margin: 10px 0;
`;

const Loader = styled.div`
  margin: 0 0 10px 0;
  border: 4px solid #f3f3f3;
  border-top: 4px solid #3498db;
  border-radius: 50%;
  width: 40px;
  height: 40px;
  animation: spin 2s linear infinite;

  @keyframes spin {
    0% { transform: rotate(0deg); }
    100% { transform: rotate(360deg); }
  }
`;

const AudioProcessor = () => {
  const [recording, setRecording] = useState(false);
  const [loading, setLoading] = useState(false);
  const [audioURL, setAudioURL] = useState('');
  const [noisyAudioURL, setNoisyAudioURL] = useState('');
  const [filteredAudioURL, setFilteredAudioURL] = useState('');
  const audioContextRef = useRef(null);
  const mediaRecorderRef = useRef(null);
  const audioChunksRef = useRef([]);

  const startRecording = () => {
    if (!audioContextRef.current) {
      audioContextRef.current = new (window.AudioContext || window.webkitAudioContext)();
    }
    setRecording(true);
    audioChunksRef.current = [];
    navigator.mediaDevices.getUserMedia({ audio: true }).then(stream => {
      mediaRecorderRef.current = new MediaRecorder(stream);
      mediaRecorderRef.current.ondataavailable = event => {
        audioChunksRef.current.push(event.data);
      };
      mediaRecorderRef.current.onstop = processAudio;
      mediaRecorderRef.current.start();
    });
  };

  const stopRecording = () => {
    setRecording(false);
    mediaRecorderRef.current.stop();
  };

  const processAudio = () => {
    setLoading(true);
    const audioBlob = new Blob(audioChunksRef.current, { type: 'audio/wav' });
    const audioUrl = URL.createObjectURL(audioBlob);
    setAudioURL(audioUrl);
    addNoiseAndFilter(audioBlob);
  };

  const handleFileUpload = event => {
    if (!audioContextRef.current) {
      audioContextRef.current = new (window.AudioContext || window.webkitAudioContext)();
    }
    const file = event.target.files[0];
    if (file) {
      setLoading(true);
      const audioUrl = URL.createObjectURL(file);
      setAudioURL(audioUrl);
      addNoiseAndFilter(file);
    }
  };

  const addNoiseAndFilter = (audioBlob) => {
    const reader = new FileReader();
    reader.readAsArrayBuffer(audioBlob);
    reader.onloadend = () => {
      if (!audioContextRef.current) {
        audioContextRef.current = new (window.AudioContext || window.webkitAudioContext)();
      }
      audioContextRef.current.decodeAudioData(reader.result, buffer => {
        const noisyBuffer = addGaussianNoise(buffer, 1);
        const noisyBlob = bufferToBlob(noisyBuffer);
        const noisyUrl = URL.createObjectURL(noisyBlob);
        setNoisyAudioURL(noisyUrl);

        const filteredBuffer = applyFIRFilter(noisyBuffer);
        const filteredBlob = bufferToBlob(filteredBuffer);
        const filteredUrl = URL.createObjectURL(filteredBlob);
        setFilteredAudioURL(filteredUrl);
        setLoading(false);
      });
    };
  };

  const addGaussianNoise = (buffer, snr) => {
    const noisyBuffer = audioContextRef.current.createBuffer(1, buffer.length, buffer.sampleRate);
    const channelData = buffer.getChannelData(0);
    const noisyData = noisyBuffer.getChannelData(0);
    const signalPower = channelData.reduce((sum, val) => sum + val * val, 0) / channelData.length;
    const noisePower = signalPower / (10 ** (snr / 10));
    for (let i = 0; i < channelData.length; i++) {
      noisyData[i] = channelData[i] + (Math.random() - 0.5) * Math.sqrt(noisePower);
    }
    return noisyBuffer;
  };

  const applyFIRFilter = (buffer) => {
    const filterCoeffs = calculateFIRCoefficients();
    const input = buffer.getChannelData(0);
    const output = new Float32Array(input.length);
    for (let i = 0; i < input.length; i++) {
      output[i] = 0;
      for (let j = 0; j < filterCoeffs.length; j++) {
        if (i - j >= 0) {
          output[i] += input[i - j] * filterCoeffs[j];
        }
      }
    }
    const filteredBuffer = audioContextRef.current.createBuffer(1, output.length, buffer.sampleRate);
    filteredBuffer.copyToChannel(output, 0);
    return filteredBuffer;
  };

  const calculateFIRCoefficients = () => {
    const N = 101; // 滤波器长度
    const fc1 = 4000 / 16000; // 归一化通带边缘频率
    const fc2 = 4500 / 16000; // 归一化阻带边缘频率
    const h = new Float32Array(N);
    const M = N - 1;
    for (let n = 0; n < N; n++) {
      if (n === M / 2) {
        h[n] = 2 * (fc2 - fc1);
      } else {
        h[n] = (Math.sin(2 * Math.PI * fc2 * (n - M / 2)) - Math.sin(2 * Math.PI * fc1 * (n - M / 2))) / (Math.PI * (n - M / 2));
      }
      h[n] *= 0.54 - 0.46 * Math.cos(2 * Math.PI * n / M); // 汉宁窗
    }
    return h;
  };

  const bufferToBlob = (buffer) => {
    const wavBuffer = audioBufferToWav(buffer);
    return new Blob([new DataView(wavBuffer)], { type: 'audio/wav' });
  };

  const audioBufferToWav = (buffer) => {
    const numOfChannels = buffer.numberOfChannels;
    const length = buffer.length * numOfChannels * 2 + 44;
    const bufferResult = new ArrayBuffer(length);
    const view = new DataView(bufferResult);
    let offset = 0;
    let pos = 0;

    const writeString = (str) => {
      for (let i = 0; i < str.length; i++) {
        view.setUint8(pos + offset, str.charCodeAt(i));
        offset++;
      }
    };

    const write16Bit = (data) => {
      view.setInt16(pos + offset, data, true);
      offset += 2;
    };

    const write32Bit = (data) => {
      view.setInt32(pos + offset, data, true);
      offset += 4;
    };

    writeString('RIFF');
    write32Bit(length - 8);
    writeString('WAVE');
    writeString('fmt ');
    write32Bit(16);
    write16Bit(1);
    write16Bit(numOfChannels);
    write32Bit(buffer.sampleRate);
    write32Bit(buffer.sampleRate * numOfChannels * 2);
    write16Bit(numOfChannels * 2);
    write16Bit(16);
    writeString('data');
    write32Bit(length - pos - 4);

    for (let i = 0; i < buffer.length; i++) {
      for (let channel = 0; channel < numOfChannels; channel++) {
        const sample = Math.max(-1, Math.min(1, buffer.getChannelData(channel)[i]));
        write16Bit(sample < 0 ? sample * 32768 : sample * 32767);
      }
    }

    return bufferResult;
  };

  return (
    <Container>
      <div>
        <Button onClick={recording ? stopRecording : startRecording}>
          {recording ? 'Stop Recording' : 'Start Recording'}
        </Button>
        <InputLabel htmlFor="audio-upload">Upload Audio</InputLabel>
        <HiddenInput type="file" id="audio-upload" accept="audio/*" onChange={handleFileUpload} />
      </div>
      {loading && <Loader />}
      {audioURL && (
        <AudioContainer>
          <AudioTitle>Original Audio</AudioTitle>
          <audio controls src={audioURL}></audio>
        </AudioContainer>
      )}
      {noisyAudioURL && (
        <AudioContainer>
          <AudioTitle>Noisy Audio</AudioTitle>
          <audio controls src={noisyAudioURL}></audio>
        </AudioContainer>
      )}
      {filteredAudioURL && (
        <AudioContainer>
          <AudioTitle>Filtered Audio</AudioTitle>
          <audio controls src={filteredAudioURL}></audio>
        </AudioContainer>
      )}
    </Container>
  );
};

export default AudioProcessor;
