import React, { useState, useRef } from 'react';
import styled from 'styled-components';

const Container = styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
  width: 100%;
  padding: 20px;
`;

const Section = styled.div`
  width: 80%;
  margin: 20px 0;
  padding: 20px;
  border: 1px solid #ccc;
  border-radius: 10px;
`;

const SectionTitle = styled.h2`
  margin-bottom: 10px;
  font-size: 24px;
`;

const Button = styled.button`
  font-size: 16px;
  padding: 10px 20px;
  margin: 10px;
  cursor: pointer;
  background-color: #4caf50;
  color: white;
  border: none;
  border-radius: 5px;
  &:hover {
    background-color: #45a049;
  }
`;

const InputLabel = styled.label`
  display: inline-block;
  font-size: 16px;
  padding: 10px 20px;
  margin: 10px;
  cursor: pointer;
  background-color: #008cba;
  color: white;
  border: none;
  border-radius: 5px;
  &:hover {
    background-color: #007bb5;
  }
`;

const Label = styled.label`
  font-size: 16px;
  padding: 10px 20px;
`;

const HiddenInput = styled.input`
  display: none;
`;

const AudioContainer = styled.div`
  margin: 20px 0;
  text-align: center;
`;

const AudioTitle = styled.h2`
  font-size: 18px;
  margin: 10px 0;
`;

const Loader = styled.div`
  border: 4px solid #f3f3f3;
  border-top: 4px solid #3498db;
  border-radius: 50%;
  width: 40px;
  height: 40px;
  animation: spin 2s linear infinite;

  @keyframes spin {
    0% { transform: rotate(0deg); }
    100% { transform: rotate(360deg); }
  }
`;

const Select = styled.select`
  padding: 10px;
  margin: 10px;
  font-size: 16px;
`;

const AudioProcessor = () => {
  const [recording, setRecording] = useState(false);
  const [loading, setLoading] = useState(false);
  const [audioURL, setAudioURL] = useState('');
  const [noisyAudioURL, setNoisyAudioURL] = useState('');
  const [filteredAudioURL, setFilteredAudioURL] = useState('');
  const audioContextRef = useRef(new (window.AudioContext || window.webkitAudioContext)());
  const mediaRecorderRef = useRef(null);
  const audioChunksRef = useRef([]);
  const [snr, setSnr] = useState(20);
  const [filterLength, setFilterLength] = useState(128);

  const startRecording = () => {
    setRecording(true);
    audioChunksRef.current = [];
    navigator.mediaDevices.getUserMedia({ audio: true }).then(stream => {
      mediaRecorderRef.current = new MediaRecorder(stream);
      mediaRecorderRef.current.ondataavailable = event => {
        audioChunksRef.current.push(event.data);
      };
      mediaRecorderRef.current.onstop = processAudio;
      mediaRecorderRef.current.start();
    });
  };

  const stopRecording = () => {
    setRecording(false);
    mediaRecorderRef.current.stop();
  };

  const processAudio = () => {
    setLoading(true);
    const audioBlob = new Blob(audioChunksRef.current, { type: 'audio/wav' });
    const audioUrl = URL.createObjectURL(audioBlob);
    setAudioURL(audioUrl);
    setLoading(false);
  };

  const handleFileUpload = (event) => {
    const file = event.target.files[0];
    if (file) {
      const audioUrl = URL.createObjectURL(file);
      setAudioURL(audioUrl);
    }
  };

  const addNoiseAndFilter = async () => {
    try {
      setLoading(true);
      const audioBuffer = await fetch(audioURL)
        .then(response => response.arrayBuffer())
        .then(arrayBuffer => audioContextRef.current.decodeAudioData(arrayBuffer));

      const noisyBuffer = addGaussianNoise(audioBuffer, snr);
      const filteredBuffer = applyBandpassFilter(noisyBuffer, filterLength, 4000, 4500, 32000);
      setNoisyAudioURL(URL.createObjectURL(bufferToWave(noisyBuffer, noisyBuffer.length)));
      setFilteredAudioURL(URL.createObjectURL(bufferToWave(filteredBuffer, filteredBuffer.length)));
    } catch (error) {
      console.error('Error processing audio:', error);
    } finally {
      setLoading(false);
    }
  };

  const addGaussianNoise = (buffer, snr) => {
    const channelData = buffer.getChannelData(0);
    const signalPower = channelData.reduce((acc, val) => acc + val * val, 0) / channelData.length;
    const noisePower = signalPower / Math.pow(10, snr / 10);
    const noise = new Float32Array(channelData.length).map(() => Math.sqrt(noisePower) * (Math.random() * 2 - 1));
    const noisyData = new Float32Array(channelData.length);
    for (let i = 0; i < channelData.length; i++) {
      noisyData[i] = channelData[i] + noise[i];
    }
    const noisyBuffer = audioContextRef.current.createBuffer(1, noisyData.length, buffer.sampleRate);
    noisyBuffer.copyToChannel(noisyData, 0);
    return noisyBuffer;
  };

  const applyBandpassFilter = (buffer, filterLength, passbandStart, passbandEnd, sampleRate) => {
    const nyquist = sampleRate / 2;
    const normalPassbandStart = passbandStart / nyquist;
    const normalPassbandEnd = passbandEnd / nyquist;
    const taps = new Float32Array(filterLength);

    for (let i = 0; i < filterLength; i++) {
      const m = i - (filterLength - 1) / 2;
      if (m === 0) {
        taps[i] = 2 * (normalPassbandEnd - normalPassbandStart);
      } else {
        taps[i] = (Math.sin(2 * Math.PI * normalPassbandEnd * m) - Math.sin(2 * Math.PI * normalPassbandStart * m)) / (Math.PI * m);
        taps[i] *= 0.54 - 0.46 * Math.cos(2 * Math.PI * i / (filterLength - 1)); // Hamming window
      }
    }

    const filteredData = new Float32Array(buffer.length);
    const channelData = buffer.getChannelData(0);
    for (let i = 0; i < buffer.length; i++) {
      let sum = 0;
      for (let j = 0; j < filterLength; j++) {
        if (i - j >= 0) {
          sum += channelData[i - j] * taps[j];
        }
      }
      filteredData[i] = sum;
    }

    const filteredBuffer = audioContextRef.current.createBuffer(1, filteredData.length, buffer.sampleRate);
    filteredBuffer.copyToChannel(filteredData, 0);
    return filteredBuffer;
  };

  const bufferToWave = (buffer, length) => {
    const numOfChannels = buffer.numberOfChannels;
    const bufferLength = length * numOfChannels * 2 + 44;
    const bufferResult = new ArrayBuffer(bufferLength);
    const view = new DataView(bufferResult);
    const channels = [];
    let pos = 0;
    let offset = 0;

    const writeString = (str) => {
      for (let i = 0; i < str.length; i++) {
        view.setUint8(pos + offset, str.charCodeAt(i));
        offset++;
      }
    };

    const write16Bit = (val) => {
      view.setInt16(pos + offset, val, true);
      offset += 2;
    };

    writeString('RIFF'); offset += 4;
    view.setUint32(pos + offset, bufferLength - 8, true); offset += 4;
    writeString('WAVE'); offset += 4;
    writeString('fmt '); offset += 4;
    view.setUint32(pos + offset, 16, true); offset += 4;
    view.setUint16(pos + offset, 1, true); offset += 2;
    view.setUint16(pos + offset, numOfChannels, true); offset += 2;
    view.setUint32(pos + offset, buffer.sampleRate, true); offset += 4;
    view.setUint32(pos + offset, buffer.sampleRate * 4, true); offset += 4;
    view.setUint16(pos + offset, numOfChannels * 2, true); offset += 2;
    view.setUint16(pos + offset, 16, true); offset += 2;
    writeString('data'); offset += 4;
    view.setUint32(pos + offset, bufferLength - pos - 8, true); offset += 4;

    for (let i = 0; i < buffer.numberOfChannels; i++) {
      channels.push(buffer.getChannelData(i));
    }

    while (pos < bufferLength) {
      for (let i = 0; i < numOfChannels; i++) {
        const sample = Math.max(-1, Math.min(1, channels[i][pos]));
        view.setInt16(pos + offset, sample < 0 ? sample * 0x8000 : sample * 0x7FFF, true);
        pos++;
      }
    }

    return new Blob([view], { type: 'audio/wav' });
  };

  return (
    <Container>
      <Section>
        <SectionTitle>输入</SectionTitle>
        <Button onClick={recording ? stopRecording : startRecording}>
          {recording ? '停止录音' : '开始录音'}
        </Button>
        <InputLabel>
          导入音频
          <HiddenInput type="file" accept="audio/*" onChange={handleFileUpload} />
        </InputLabel>
        {audioURL && (
          <AudioContainer>
            <AudioTitle>录音</AudioTitle>
            <audio controls src={audioURL} />
          </AudioContainer>
        )}
      </Section>
      <Section>
        <SectionTitle>加噪</SectionTitle>
        <Label>
          信噪比 (dB):
          <Select value={snr} onChange={e => setSnr(e.target.value)}>
            {[5, 10, 15, 20, 25, 30].map(value => (
              <option key={value} value={value}>
                {value}
              </option>
            ))}
          </Select>
        </Label>
        <Button onClick={addNoiseAndFilter}>加噪并降噪</Button>
        {loading && <Loader />}
        {noisyAudioURL && (
          <AudioContainer>
            <AudioTitle>加噪</AudioTitle>
            <audio controls src={noisyAudioURL} />
          </AudioContainer>
        )}
        {filteredAudioURL && (
          <AudioContainer>
            <AudioTitle>降噪</AudioTitle>
            <audio controls src={filteredAudioURL} />
          </AudioContainer>
        )}
      </Section>
    </Container>
  );
};

export default AudioProcessor;
